import Ticket from"../../../user-ticket/backend/models/ticketModel.js";export const getAllTickets=async(s,t)=>{try{const s=await Ticket.find();t.status(200).json({success:!0,data:s})}catch(s){t.status(400).json({success:!1,error:s.message})}};export const getIssueTypes=async(s,t)=>{try{const s=await Ticket.distinct("issue_type");t.status(200).json({success:!0,data:s})}catch(s){t.status(500).json({success:!1,error:s.message})}};export const getTicketsByIssueType=async(s,t)=>{try{const{issueType:e}=s.params,c=await Ticket.find({issue_type:e});t.status(200).json({success:!0,data:c})}catch(s){t.status(400).json({success:!1,error:s.message})}};