import Ticket from"../models/ticketModel.js";export const getIssueTypes=async(s,t)=>{try{const s=await Ticket.distinct("issue_type");t.status(200).json(s)}catch(s){t.status(500).json({message:"Error fetching issue types"})}};export const getAllTickets=async(s,t)=>{try{const s=await Ticket.find({});t.status(200).json(s)}catch(s){t.status(500).json({message:"Error fetching tickets"})}};export const getTicketsByIssueType=async(s,t)=>{const{issue_type:e}=s.params;try{const s=await Ticket.find({issue_type:e});t.status(200).json(s)}catch(s){t.status(500).json({message:"Error fetching tickets by issue type"})}};